INSERT INTO `sms_forum_settinggroups` (`gid`, `name`, `title`, `description`, `disporder`, `isdefault`) VALUES
(1, 'onlineoffline', 'Board Online / Offline', 'These settings allow you to globally turn your forums online or offline, and allow you to specify a reason for turning them off.', 1, 1),
(2, 'details', 'Site Details', 'This section contains various settings such as your board name and url, as well as your website name and url.', 2, 1),
(3, 'general', 'General Configuration', 'This section allows you to manage the general aspects of your board.', 3, 1),
(4, 'server', 'Server and Optimization Options', 'These options allow you to set various server and optimization preferences allowing you to reduce the load on your server, and gain better performance on your board.', 4, 1),
(5, 'datetime', 'Date and Time Formats', 'Here you can specify the different date and time formats used to display dates and times on the forums.', 5, 1),
(6, 'forumhome', 'Forum Home Options', 'This section allows you to manage the various settings used on the forum home (index.php) of your boards such as enabling and disabling different features.', 6, 1),
(7, 'forumdisplay', 'Forum Display Options', 'This section allows you to manage the various settings used on the forum display (forumdisplay.php) of your boards such as enabling and disabling different features.', 7, 1),
(8, 'showthread', 'Show Thread Options', 'This section allows you to manage the various settings used on the thread display page (showthread.php) of your boards such as enabling and disabling different features.', 8, 1),
(9, 'member', 'Login and Registration Options', 'Here you can control various settings with relation to user account registration and login management.', 9, 1),
(10, 'profile', 'Profile Options', 'The options here control the various settings of user profiles around the forums.', 10, 1),
(11, 'posting', 'Posting', 'These options control the various elements in relation to posting messages on the forums.', 11, 1),
(12, 'attachments', 'Attachments', 'Various options related to the attachment system can be managed and set here.', 12, 1),
(13, 'memberlist', 'Member List', 'This section allows you to control various aspects of the board member listing (memberlist.php), such as how many members to show per page, and which features to enable or disable.', 13, 1),
(14, 'reputation', 'Reputation', 'The reputation system allows your users to rate others and leave a comment on the user. This section has settings to disable and change other aspects of the reputation page (reputation.php).', 14, 1),
(15, 'warning', 'Warning System Settings', 'The warning system allows forum staff to warn users for rule violations. Here you can manage the settings that control the warning system (warnings.php).', 15, 1),
(16, 'privatemessaging', 'Private Messaging', 'Various options with relation to the MyBB Private Messaging system (private.php) can be managed and set here.', 16, 1),
(17, 'calendar', 'Calendar', 'The board calendar allows the public and private listing of events and members\' birthdays. This section allows you to control and manage the settings for the Calendar (calendar.php).', 17, 1),
(18, 'whosonline', 'Who\'s Online', 'Various settings regarding the Who is Online functionality (online.php).', 18, 1),
(19, 'userpruning', 'User Pruning', 'User Pruning allows you to remove users from your forum meeting certain criteria. Here you can configure that criteria.', 19, 1),
(20, 'portal', 'Portal Settings', 'The portal page compiles several different pieces of information about your forum, including latest posts, who\'s online, forum stats, announcements, and more. This section has settings to control the aspects of the portal page (portal.php).', 20, 1),
(21, 'search', 'Search System', 'The various settings in this group allow you to make changes to the built in search mechanism for threads, posts and help documents in MyBB.', 21, 1),
(22, 'clickablecode', 'Clickable Smilies and BB Code', 'This section allows you to change how the clickable smilies inserter appears and control the default MyCodes.', 22, 1),
(23, 'cpprefs', 'Control Panel Preferences (Global)', 'This section allows you to set the global preferences for the Admin Control Panel.', 23, 1),
(24, 'mailsettings', 'Mail Settings', 'This section allows you to control various aspects of the MyBB mail system, such as sending with PHP mail or with a off server SMTP server.', 24, 1),
(25, 'contactsettings', 'Contact Settings', 'The various options to change the behaviour of the contact page (contact.php).', 25, 1),
(26, 'purgespammer', 'Purge Spammer', 'This section allows you to change the settings of the Purge Spammer feature.', 26, 1),
(27, 'stopforumspam', 'Stop Forum Spam', 'This section allows you to change the settings used for the integration with StopForumSpam.com', 27, 1),
(28, 'contactdetails', 'Contact Details', 'This section allows you to change the settings of the contact fields feature.', 28, 1),
(29, 'statspage', 'Statistics Page', 'This section allows you to change the settings of the statistics page.', 28, 1);
INSERT INTO `sms_forum_settings` (`sid`, `name`, `title`, `description`, `optionscode`, `value`, `disporder`, `gid`, `isdefault`) VALUES
(1, 'boardclosed', 'Board Closed', 'If you need to close your forums to make some changes or perform an upgrade, this is the global switch. Viewers will not be able to view your forums, however, they will see a message with the reason you specify below. Administrators will still be able to view the forums.', 'yesno', '0', 1, 1, 1),
(2, 'boardclosed_reason', 'Board Closed Reason', 'If your forum is closed, you can set a message here that your visitors will be able to see when they visit your forums. You can leave this empty to use the language files.', 'textarea', 'These forums are currently closed for maintenance. Please check back later.', 2, 1, 1),
(3, 'bbname', 'Board Name', 'The name of your community. We recommend that it is not over 75 characters.', 'text', 'Forums', 1, 2, 1),
(4, 'bburl', 'Board URL', 'The url to your forums.<br />Include the http://. Do NOT include a trailing slash.', 'text', 'http://smswaterpolo.club/Forum', 2, 2, 1),
(5, 'homename', 'Homepage Name', 'The name of your homepage. This will appear in the footer with a link to it.', 'text', 'SMSWATER POLO FORUM', 3, 2, 1),
(6, 'homeurl', 'Homepage URL', 'The full URL of your homepage. This will be linked to in the footer along with its name.', 'text', 'http://smswaterpolo.club/', 4, 2, 1),
(7, 'adminemail', 'Admin Email', 'The administrator\'s email address. This will be used for outgoing emails sent via the forums.', 'text', 'info@smswaterpolo.club', 5, 2, 1),
(8, 'returnemail', 'Return Email', 'The email address for incoming replies to outgoing emails sent via the forums and is useful for no-reply accounts. Leave blank to use the admin email address instead.', 'text', '', 6, 2, 1),
(9, 'contactemail', 'Contact Email', 'The email address that messages sent via the contact form (contact.php) will be sent to. Leave blank to use the admin email address instead.', 'text', '', 7, 2, 1),
(10, 'contactlink', 'Contact Us Link', 'This will be used for the Contact Us link on the bottom of all the forum pages. Can either be an email address (using mailto:email@website.com) or a hyperlink.', 'text', 'contact.php', 8, 2, 1),
(11, 'cookiedomain', 'Cookie Domain', 'The domain which cookies should be set to. This can remain blank. It should also start with a . so it covers all subdomains.', 'text', '.smswaterpolo.club', 9, 2, 1),
(12, 'cookiepath', 'Cookie Path', 'The path which cookies are set to. We recommend setting this to the full directory path to your forums with a trailing slash.', 'text', '/Forum/', 10, 2, 1),
(13, 'cookieprefix', 'Cookie Prefix', 'A prefix to append to all cookies set by MyBB. This is useful if you wish to install multiple copies of MyBB on the one domain or have other software installed which conflicts with the names of the cookies in MyBB. If not specified, no prefix will be used.', 'text', '', 11, 2, 1),
(14, 'cookiesecureflag', 'Secure Cookie Flag', 'Cookies can be set with the Secure flag to prevent them from being sent over unencrypted connections. You should enable this setting only if your forum works correctly under HTTPS.', 'yesno', '0', 12, 2, 1),
(15, 'showvernum', 'Show Version Numbers', 'Allows you to turn off the public display of version numbers in MyBB.', 'onoff', '0', 13, 2, 1),
(16, 'mailingaddress', 'Mailing Address', 'If you have a mailing address, enter it here. This is shown on the COPPA compliance form.', 'textarea', '', 14, 2, 1),
(17, 'faxno', 'Contact Fax No', 'If you have a fax number, enter it here. This is shown on the COPPA compliance form.', 'text', '', 15, 2, 1),
(18, 'bblanguage', 'Default Language', 'The default language that MyBB should use for guests and for users without a selected language in their user control panel.', 'language', 'english', 1, 3, 1),
(19, 'captchaimage', 'CAPTCHA Images for Registration & Posting', 'To help prevent automated registrations and postings, you can choose a type of image verification for the user to complete. Please see the MyBB Docs for more information on CAPTCHAs.', 'select\r\n0=No CAPTCHA\r\n1=MyBB Default CAPTCHA\r\n2=reCAPTCHA\r\n4=NoCAPTCHA reCAPTCHA', '1', 2, 3, 1),
(20, 'captchapublickey', 'reCAPTCHA Public Key', 'Your reCAPTCHA public key.', 'text', '', 3, 3, 1),
(21, 'captchaprivatekey', 'reCAPTCHA Private Key', 'Your reCAPTCHA private key.', 'text', '', 4, 3, 1),
(22, 'reportmethod', 'Reported Content Notification Method', 'Please select from the list how you would like moderators to be notified when content is reported. Note: Moderators are only notified for posts in forums they moderate or all content if they are a super moderator.', 'radio\r\ndb=Shown in the Mod CP only\r\npms=Sent as Private Messages and shown in the Mod CP\r\nemail=Sent via Email and shown in the Mod CP', 'db', 5, 3, 1),
(23, 'decpoint', 'Decimal Point', 'The decimal point you use in your region.', 'text', '.', 6, 3, 1),
(24, 'thousandssep', 'Thousands Numeric Separator', 'The punctuation you want to use. For example, a setting of \',\' with the number 1200 will give you 1,200.', 'text', ',', 7, 3, 1),
(25, 'showlanguageselect', 'Show Language Selector in Footer', 'Set to No if you do not want to show the language selection area in the footer of all pages in the board. If you only have one language installed this setting will be ignored.', 'yesno', '1', 8, 3, 1),
(26, 'showthemeselect', 'Show Theme Selector in Footer', 'Set to No if you do not want to show the theme selection area in the footer of all pages in the board.', 'yesno', '1', 9, 3, 1),
(27, 'maxmultipagelinks', 'Maximum Page Links in Pagination', 'Here you can set the number of next and previous page links to show in the pagination for threads or forums with more than one page of results. Set to 0 to disable the limitation.', 'numeric\r\nmin=1', '5', 10, 3, 1),
(28, 'jumptopagemultipage', 'Show Jump To Page form in Pagination', 'Do you want to show a \"jump to page\" form in pagination if number of pages exceeds \"Maximum Page Links in Pagination\"?', 'yesno', '1', 11, 3, 1),
(29, 'no_plugins', 'Disable All Plugins', 'Setting this to Yes will disable all plugins without deactivating or uninstalling them. This is equivalent of manually defining NO_PLUGINS at the top of ./inc/init.php.', 'yesno', '0', 12, 3, 1),
(30, 'deleteinvites', 'Expire Old Group Invites', 'The number of days until any pending invites to user groups expire. Set to 0 to disable.', 'numeric\r\nmin=0', '180', 13, 3, 1),
(31, 'redirects', 'Friendly Redirection Pages', 'This will enable friendly redirection pages instead of bumping the user directly to the page.', 'onoff', '1', 14, 3, 1),
(32, 'enableforumjump', 'Enable Forum Jump Menu?', 'The forum jump menu is shown on the forum and thread view pages. It can add significant load to your forums if you have a large amount of forums. Set to \'No\' to disable it.', 'yesno', '1', 15, 3, 1),
(33, 'seourls', 'Enable search engine friendly URLs?', 'Search engine friendly URLs change the MyBB links to shorter URLs which search engines prefer and are easier to type. showthread.php?tid=1 becomes thread-1.html. <strong>Once this setting is enabled you need to make sure you have the MyBB .htaccess in your MyBB root directory (or the equivalent for your web server). Automatic detection may not work on all servers.</strong> Please see the <a href=\"https://docs.mybb.com/1.8/administration/configuring-search-engine-friendly-URLs\">MyBB Docs</a> for assistance.', 'select\r\nauto=Automatic Detection\r\nyes=Enabled\r\nno=Disabled', 'auto', 1, 4, 1),
(34, 'seourls_archive', 'Enable search engine friendly URLs in Archive?', 'Search engine friendly URLs can be used in the archive. <strong>Once this setting is enabled ensure the archive still works as expected.</strong>', 'yesno', '0', 2, 4, 1),
(35, 'gzipoutput', 'Use GZip Page Compression?', 'Do you want to compress pages in GZip format when they are sent to the browser? This means quicker downloads for your visitors, and less traffic usage for you.', 'yesno', '0', 3, 4, 1),
(36, 'gziplevel', 'GZip Page Compression Level', 'Set the level for GZip Page Compression from 0-9.  (0=no compression, 9=maximum compression). A level of 4 is recommended for most installations.', 'numeric\r\nmin=0\r\nmax=9', '4', 4, 4, 1),
(37, 'nocacheheaders', 'Send No Cache Headers', 'With this option you can prevent caching of the page by the browser.', 'yesno', '0', 5, 4, 1),
(38, 'load', '*NIX Load Limiting', 'Limit the maximum server load before MyBB rejects people. 0 for none. Recommended limit is 5.0.', 'text', '0', 6, 4, 1),
(39, 'tplhtmlcomments', 'Output template start/end comments?', 'This will enable or disable the output of template start/end comments in the HTML.', 'yesno', '1', 7, 4, 1),
(40, 'use_xmlhttprequest', 'Enable XMLHttp request features?', 'This will enable or disable the XMLHttp request features.', 'yesno', '1', 8, 4, 1),
(41, 'extraadmininfo', 'Advanced Stats / Debug information', 'Shows Server load, generation time, memory usage, etc on the bottom of all pages in the root folder. Please note that only administrators see this information.', 'yesno', '1', 9, 4, 1),
(42, 'uploadspath', 'Uploads Path', 'The path used for all board uploads. It <b>must be chmod 777</b> (on *nix servers).', 'text', './uploads', 10, 4, 1),
(43, 'useerrorhandling', 'Use Error Handling', 'If you do not wish to use the integrated error handling for MyBB, you may turn this option off. However, it is recommended that it stay on', 'onoff', '1', 11, 4, 1),
(44, 'errorlogmedium', 'Error Logging Medium', 'The type of the error handling to use.', 'select\r\nnone=Neither\r\nlog=Log errors\r\nemail=Email errors\r\nboth=Log and email errors\r\n', 'none', 12, 4, 1),
(45, 'errortypemedium', 'Error Type Medium', 'The type of errors to show. It is recommended you hide errors and warnings on a production forum and log them instead.', 'select\r\nwarning=Warnings\r\nerror=Errors\r\nboth=Warnings and Errors\r\nnone=Hide Errors and Warnings\r\n', 'both', 13, 4, 1),
(46, 'errorloglocation', 'Error Logging Location', 'The location of the log to send errors to, if specified.', 'text', './error.log', 14, 4, 1),
(47, 'ip_forwarded_check', 'Scrutinize User\'s IP address?', 'Do you want to check a user\'s IP address for HTTP_X_FORWARDED_FOR or HTTP_X_REAL_IP headers? If you\'re unsure, set this to No.', 'yesno', '0', 15, 4, 1),
(48, 'minifycss', 'Minify Stylesheets?', 'Do you want to minify stylesheets? Choosing Yes can save bandwidth and overall provide faster page loads.', 'yesno', '0', 16, 4, 1),
(49, 'usecdn', 'Use a CDN?', 'You can utilise a CDN (Content Delivery Network) to offload the loading of static files such as Stylesheets, JavaScript and Images. Do you wish to enable this functionality?', 'yesno', '0', 17, 4, 1),
(50, 'cdnurl', 'URL to use for static files', 'If you have enabled the CDN option above, please enter the base URL to serve static content from. This should be a valid URL without a trailing slash.', 'text', '', 18, 4, 1),
