(214, 'searchtype', 'Search Type', 'Please select the type of search system you wish to use. You can either chose between \"Standard\", or \"Full Text\" (depending on your database). Fulltext searching is more powerful than the standard MyBB searching and quicker too.', 'php\r\n<select name=\\\"upsetting[{$setting[\'name\']}]\\\"><option value=\\\"standard\\\">\".($lang->setting_searchtype_standard?$lang->setting_searchtype_standard:\"Standard\").\"</option>\".($db->supports_fulltext(\"threads\") && $db->supports_fulltext_boolean(\"posts\")?\"<option value=\\\"fulltext\\\"\".($setting[\'value\']==\"fulltext\"?\" selected=\\\"selected\\\"\":\"\").\">\".($lang->setting_searchtype_fulltext?$lang->setting_searchtype_fulltext:\"Full Text\").\"</option>\":\"\").\"</select>', 'standard', 1, 21, 1),
(215, 'searchfloodtime', 'Search Flood Time (seconds)', 'Enter the time in seconds for the minimum allowed interval for searching. This prevents users from overloading your server by constantly performing searches. Set to 0 to disable.', 'numeric\r\nmin=0', '30', 2, 21, 1),
