(1, 'announcement', '<html>\r\n<head>\r\n<title>{$lang->forum_announcement}</title>\r\n{$headerinclude}\r\n<script type=\"text/javascript\">\r\nvar announcement_quickdelete_confirm = \"{$lang->announcement_quickdelete_confirm}\";\r\n</script>\r\n</head>\r\n<body>\r\n{$header}\r\n<table border=\"0\" cellspacing=\"{$theme[\'borderwidth\']}\" cellpadding=\"{$theme[\'tablespace\']}\" class=\"tborder tfixed clear no_bottom_border\">\r\n<tr>\r\n<td class=\"thead\"><strong>{$lang->forum_announcement}</strong></td>\r\n</tr>\r\n<tr><td id=\"posts_container\">\r\n	<div id=\"posts\">\r\n	{$announcement}\r\n	</div>\r\n</td></tr>\r\n</table>\r\n{$footer}\r\n<script type=\"text/javascript\">\r\n	$(\".author_avatar img\").error(function () {\r\n		$(this).unbind(\"error\").closest(\'.author_avatar\').remove();\r\n	});\r\n</script>\r\n</body>\r\n</html>', -2, '1804', '', 1492394902),
(2, 'announcement_edit', '<a href=\"modcp.php?action=edit_announcement&amp;aid={$post[\'aid\']}\" title=\"{$lang->announcement_edit}\" class=\"postbit_edit\"><span>{$lang->postbit_button_edit}</span></a>', -2, '1800', '', 1492394902),
(3, 'announcement_quickdelete', '<a href=\"modcp.php?action=delete_announcement&amp;aid={$post[\'aid\']}&amp;my_post_key={$mybb->post_code}\" onclick=\"MyBB.deleteAnnouncement(this); return false;\" class=\"postbit_qdelete\"><span>{$lang->postbit_button_qdelete}</span></a>', -2, '1800', '', 1492394902),
(4, 'calendar', '<html>\r\n<head>\r\n	<title>{$mybb->settings[\'bbname\']} - {$lang->calendar}</title>\r\n	{$headerinclude}\r\n</head>\r\n<body>\r\n	{$header}\r\n	<table border=\"0\" cellspacing=\"{$theme[\'borderwidth\']}\" cellpadding=\"{$theme[\'tablespace\']}\" class=\"tborder\">\r\n		<thead>\r\n			<tr>\r\n				<td class=\"thead\" colspan=\"8\">\r\n					<div class=\"float_right\">\r\n						<a href=\"{$prev_link}\">&laquo; {$prev_month[\'name\']} {$prev_month[\'year\']}</a> | <a href=\"{$next_link}\">{$next_month[\'name\']} {$next_month[\'year\']} &raquo;</a>\r\n					</div>\r\n					<div><strong>{$monthnames[$month]} {$year}</strong></div>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td class=\"tcat\">&nbsp;</td>\r\n			{$weekday_headers}\r\n			</tr>\r\n		</thead>\r\n		<tbody>\r\n		{$calendar_rows}\r\n		</tbody>\r\n	</table>\r\n<br />\r\n<form action=\"calendar.php\" method=\"post\">\r\n	<table border=\"0\" cellspacing=\"{$theme[\'borderwidth\']}\" cellpadding=\"{$theme[\'tablespace\']}\" class=\"tborder\">\r\n		<tr>\r\n			<td class=\"trow1\">\r\n				<table width=\"100%\" cellspacing=\"0\" cellpadding=\"0\" border=\"0\">\r\n					<tr>\r\n						<td valign=\"top\">{$addevent}</td>\r\n						<td align=\"right\">\r\n						<span class=\"smalltext\"><strong>{$lang->jump_month}</strong></span>\r\n						<select name=\"month\">\r\n							<option value=\"{$month}\">{$monthnames[$month]}</option>\r\n							<option value=\"{$month}\">----------</option>\r\n							<option value=\"1\">{$lang->alt_month_1}</option>\r\n							<option value=\"2\">{$lang->alt_month_2}</option>\r\n							<option value=\"3\">{$lang->alt_month_3}</option>\r\n							<option value=\"4\">{$lang->alt_month_4}</option>\r\n							<option value=\"5\">{$lang->alt_month_5}</option>\r\n							<option value=\"6\">{$lang->alt_month_6}</option>\r\n							<option value=\"7\">{$lang->alt_month_7}</option>\r\n							<option value=\"8\">{$lang->alt_month_8}</option>\r\n							<option value=\"9\">{$lang->alt_month_9}</option>\r\n							<option value=\"10\">{$lang->alt_month_10}</option>\r\n							<option value=\"11\">{$lang->alt_month_11}</option>\r\n							<option value=\"12\">{$lang->alt_month_12}</option>\r\n						</select>\r\n						<select name=\"year\">\r\n							<option value=\"{$year}\">{$year}</option>\r\n							<option value=\"{$year}\">----------</option>\r\n							{$yearsel}\r\n						</select>\r\n						{$gobutton}\r\n						<br />\r\n						{$calendar_jump}\r\n						</td>\r\n					</tr>\r\n				</table>\r\n			</td>\r\n		</tr>\r\n		</table>\r\n	</form>\r\n{$footer}\r\n</body>\r\n</html>', -2, '1800', '', 1492394902),
